CREATE TABLE USER (USER_ID VARCHAR(32) NOT NULL, NAME VARCHAR(32), CREATE_TS TIMESTAMP, UPDATE_TS TIMESTAMP, PRIMARY KEY(USER_ID));
CREATE TABLE LOCATION (LOCATION_ID VARCHAR(32) NOT NULL,USER_ID VARCHAR(32) NOT NULL, LATITUDE DECIMAL(10, 6), LONGITUDE DECIMAL(10, 6), CLIENT_TS TIMESTAMP, SERVER_TS TIMESTAMP, PRIMARY KEY(LOCATION_ID), FOREIGN KEY(USER_ID) REFERENCES USER(USER_ID));